INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32514, 12802, 'Ziprasidone', 'Weight Gain', 'Weight gain has been observed with atypical antipsychotic use.  While all agents in the class have been shown to produce some changes, each drug has its own specific risk profile.  When treating pediatric patients with atypical antipsychotic agents, weight gain should be monitored and assessed against that expected for normal growth.  Monitor weight at baseline and frequently thereafter.', '2', '', 'DDInter', 0);
