INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32518, 12802, 'Ziprasidone', 'Hyperprolactinemia', 'The chronic use of neuroleptic agents can cause persistent elevations in prolactin levels due to antagonism of dopamine D2 receptors.  Based on in vitro data, approximately one-third of human breast cancers are thought to be prolactin-dependent.  The clinical significance of this observation with respect to long-term neuroleptic therapy is unknown.  Chronic administration of neuroleptic drugs has been associated with mammary tumorigenesis in rodent studies but not in human clinical or epidemiologic studies.  Until further data are available, therapy with neuroleptic agents should be administered cautiously in patients with a previously detected breast cancer.  Caution is also advised in patients with preexisting hyperprolactinemia.  Hyperprolactinemia may suppress hypothalamic gonadotrophin releasing hormone (GnRH), resulting in reduced pituitary gonadotropin secretion.  This, in turn, may inhibit reproductive function by impairing gonadal steroidogenesis in both female and male patients.  Galactorrhea, amenorrhea, gynecomastia, and impotence have been reported in patients receiving prolactin-elevating compounds; however, the clinical significance of elevated serum prolactin levels is unknown for most patients.  Long-standing hyperprolactinemia when associated with hypogonadism may lead to decreased bone density in both female and male patients.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32519, 29963, 'Ziprasidone', 'Hyperprolactinemia', 'The chronic use of neuroleptic agents can cause persistent elevations in prolactin levels due to antagonism of dopamine D2 receptors.  Based on in vitro data, approximately one-third of human breast cancers are thought to be prolactin-dependent.  The clinical significance of this observation with respect to long-term neuroleptic therapy is unknown.  Chronic administration of neuroleptic drugs has been associated with mammary tumorigenesis in rodent studies but not in human clinical or epidemiologic studies.  Until further data are available, therapy with neuroleptic agents should be administered cautiously in patients with a previously detected breast cancer.  Caution is also advised in patients with preexisting hyperprolactinemia.  Hyperprolactinemia may suppress hypothalamic gonadotrophin releasing hormone (GnRH), resulting in reduced pituitary gonadotropin secretion.  This, in turn, may inhibit reproductive function by impairing gonadal steroidogenesis in both female and male patients.  Galactorrhea, amenorrhea, gynecomastia, and impotence have been reported in patients receiving prolactin-elevating compounds; however, the clinical significance of elevated serum prolactin levels is unknown for most patients.  Long-standing hyperprolactinemia when associated with hypogonadism may lead to decreased bone density in both female and male patients.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32520, 12802, 'Ziprasidone', 'Liver Diseases', 'Most neuroleptic agents are extensively metabolized by the liver.  The plasma concentrations of these agents may be increased and the half-lives prolonged in patients with impaired hepatic function.  Therapy with neuroleptic agents should be administered cautiously in patients with significant liver disease.  Lower initial dosages and slower titration may be appropriate.', '2', '', 'DDInter', 0);
