INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32568, 1970, 'Bevacizumab', 'Proteinuria', 'The incidence and severity of proteinuria is increased in patients taking inhibitors of vascular endothelial growth factor receptors (VEGFR).  Therapy with these agents should be administered cautiously in patients with renal dysfunction.  Monitoring for proteinuria and hematuria is recommended and perform baseline and periodic urinalyses during treatment, with follow up measurement as clinically indicated.  It is recommended to temporarily suspend these agents in patients with moderate to severe proteinuria and to discontinue therapy in patients with nephrotic syndrome.', '2', '', 'DDInter', 0);
