INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32795, 0, 'Betrixaban', 'Hemorrhage', 'The use of betrixaban is contraindicated in patients with active pathological bleeding.  Care and close monitoring is recommended when using this agent in patients who are receiving neuraxial anesthesia or undergoing spinal puncture as epidural or spinal hematomas may occur.  Consider risk of using this agent when scheduling patients for spinal procedures.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32796, 0, 'Betrixaban', 'Liver Diseases', 'The use of betrixaban is not recommended in patients with hepatic impairment as these patients may have intrinsic coagulation abnormalities.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32797, 0, 'Betrixaban', 'Kidney Diseases', 'Patients with severe renal impairment (CrCl >= 15 to < 30 mL/min computed by Cockcroft-Gault using actual body weight) may have an increased risk of bleeding events.  It is recommended to reduce the betrixaban dose in patients with severe renal impairment.  Monitor patients closely and promptly evaluate any signs or symptoms of blood loss in these patients.  No dose adjustment is needed for mild or moderate renal impairment (CrCl > 30 mL/min, computed by Cockcroft-Gault using actual body weight).', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32798, 0, 'Betrixaban', 'Antiphospholipid Syndrome', 'Direct acting oral anticoagulants including factor Xa inhibitors and some thrombin inhibitors as dabigatran, are not recommended for use in patients with antiphospholipid syndrome (APS).  Treatment with these drugs has been associated with increased rates of recurrent thrombotic events, especially in patients with triple positive APS.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32799, 0, 'Abaloparatide', 'Hyperparathyroidism', 'Patients known to have an underlying hypercalcemic disorder, such as primary hyperparathyroidism, should not be treated with parathyroid hormone and its analogs because of the risk of exacerbating hypercalcemia.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32800, 0, 'Abaloparatide', 'Neoplasms', 'Parathyroid hormone and its analogs should not be used in patients who are at increased baseline risk for osteosarcoma.  These agents should only be used if the potential benefits are considered to outweigh the potential risks.  Monitor these patients according to clinical guidelines.  Patients with bone metastases or a history of skeletal malignancies should not be treated with these agents.', '3', '', 'DDInter', 0);
