INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32801, 0, 'Abaloparatide', 'Urolithiasis', 'No dosage adjustment is required for patients with mild, moderate, or severe renal impairment.  Patients with severe renal impairment may have increased abaloparatide exposure that may increase the risk of adverse reactions; therefore, monitor for adverse reactions.  Abaloparatide may exacerbate urolithiasis in patients with active or a history of urolithiasis.  If active urolithiasis or preexisting hypercalciuria is suspected, measurement of urinary calcium excretion should be considered.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32802, 15926, 'Bicalutamide', 'Liver Diseases', 'Bicalutamide is extensively metabolized by the liver and its metabolism may be altered and/or delayed in patients with moderate to severe hepatic impairment.  Therapy with bicalutamide should be administered cautiously in patients with moderate to severe hepatic impairment.  Additionally there have been postmarketing reports of death or hospitalization due to severe liver injury (hepatic failure) in association with bicalutamide, so extra caution is advised in patients with hepatic impairment.  Patients should be instructed to immediately report any sign or symptoms of hepatic dysfunction such as jaundice, dark urine, right upper quadrant pain, or anorexia.  Clinical monitoring of hepatic function is recommended.  Although hepatic abnormalities such as elevated liver transaminase levels and jaundice are usually reversible, bicalutamide therapy should be discontinued if transaminase levels exceed two times the upper normal limits.', '2', '', 'DDInter', 0);
