INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32846, 1485, 'Betaxolol (ophthalmic)', 'Diabetes Mellitus', 'Beta-adrenergic receptor blocking agents (aka beta-blockers) may mask symptoms of hypoglycemia such as tremors, tachycardia and blood pressure changes.  In addition, the nonselective beta-blockers (e.g., timolol, carteolol) may inhibit catecholamine-mediated glycogenolysis, thereby potentiating insulin-induced hypoglycemia and delaying the recovery of normal blood glucose levels.  Since topically applied beta-blockers are systemically absorbed and may produce clinically significant systemic effects even at low or undetectable plasma levels, therapy with ophthalmic beta-blockers should be administered cautiously in patients with diabetes or predisposed to spontaneous hypoglycemia.', '3', '', 'DDInter', 0);
