INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32936, 69, 'Acitretin', 'Alcoholism', 'Like other retinoids, acitretin is a teratogen.  The concurrent use of alcohol and acitretin has been associated with the formation of etretinate, a related retinoid drug with a considerably longer half-life (mean: 120 days vs. 49 hours).  Alcohol should be avoided during acitretin therapy and for two months after completion of therapy in female patients of child-bearing age, since it may prolong the duration of teratogenic potential of the drug.', '2', '', 'DDInter', 0);
