INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (33602, 5164, 'Bortezomib', 'Liver Diseases', 'Cases of acute liver failure and other hepatic reactions such as hepatitis, increases in liver enzymes, and hyperbilirubinemia have been reported in patients receiving bortezomib.  Monitoring is recommended and caution should be used when using bortezomib in patients with hepatic impairment.  The exposure of bortezomib is increased in patients with moderate and severe hepatic impairment, and starting dose should be reduced in those patients.', '2', '', 'DDInter', 0);
