INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (33617, 5164, 'Bortezomib', 'Lung Diseases', 'There have been reports of Acute Respiratory Distress Syndrome (ARDS) and acute diffuse infiltrative pulmonary disease of unknown etiology in patients receiving bortezomib.  Additionally, there have been reports of pulmonary hypertension associated with bortezomib administration in the absence of left heart failure or significant pulmonary disease.  Treatment interruption should be considered in the event of new or worsening cardiopulmonary symptoms until a comprehensive diagnostic evaluation is conducted.  Caution should be exercised when using bortezomib in patients with cardiopulmonary pathologies.', '2', '', 'DDInter', 0);
