INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (33622, 15449, 'Bosentan', 'Liver Failure', 'Endothelin receptor antagonists (ERAs) have been associated with aminotransferase elevations, hepatotoxicity, and cases of liver failure.  Hepatic function should be assessed before treatment initiation and closely monitored during treatment.  Therapy should be discontinued if aminotransferase elevations are accompanied by increases in bilirubin, or clinical symptoms of hepatotoxicity such as nausea, vomiting, fever, abdominal pain or jaundice.  Treatment should be avoided in patients with elevated aminotransferases at baseline as monitoring for hepatotoxicity may be more difficult.  In general, the use of ERAs is not recommended for patients with moderate to severe hepatic impairment.', '3', '', 'DDInter', 0);
