INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (33625, 15449, 'Bosentan', 'Anemia', 'Clinical studies have shown that most endothelin receptor antagonists decrease hemoglobin concentration and hematocrit levels.  There are some postmarketing reports of anemia cases which needed blood transfusion.  Hemoglobin and hematocrit levels should be measured prior to treatment initiation and should be monitored periodically thereafter.  Initiation of therapy is not recommended in patients with clinically significant anemia.', '2', '', 'DDInter', 0);
