INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (33735, 2718, 'Bosutinib', 'Liver Diseases', 'Bosutinib is primarily metabolized by the CYP 450 3A4.  Therapy with bosutinib should be administered cautiously in patients with hepatic impairment and the starting dose should be 200 mg daily in patients with baseline hepatic impairment.  The manufacturer recommends performing hepatic enzyme tests monthly for the first three months of bosutinib treatment and as clinically indicated.  In patients with transaminase elevations, monitor liver enzymes more frequently.  Withhold, reduce dose, or discontinue bosutinib as necessary.', '2', '', 'DDInter', 0);
