INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (33738, 2718, 'Bosutinib', 'Kidney Diseases', 'An on-treatment decline in estimated glomerular filtration rate (eGFR) has occurred in patients treated with bosutinib.  The manufacturer recommends reducing the bosutinib starting dose in patients with severe (CrCl) less than 30 mL/min) or moderate (CrCl 30 to 50 mL/min) renal impairment at baseline.  Consider dose adjustment in patients with baseline and treatment emergent renal impairment.  It is recommended to monitor patients for renal function at baseline and during therapy with bosutinib, with particular attention to those patients who have preexisting renal impairment or risk factors for renal dysfunction.', '2', '', 'DDInter', 0);
