INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34071, 1780, 'Budesonide (nasal)', 'Osteoporosis', 'Prolonged use of inhaled corticosteroids may be associated with a reduction in bone density.  This effect appears to be dose-related and has been reported primarily with high dosages (>= 800 mcg/day of beclomethasone or equivalent for >= 1 year).  Reduced levels of total body calcium have also been demonstrated in patients receiving lower dosages.  Long-term therapy with inhaled and nasal corticosteroids should be administered cautiously in patients with osteoporosis.  It is important that the recommended dosages of the individual products not be exceeded and that the lowest effective dosage be used.', '2', '', 'DDInter', 0);
