INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34203, 1777, 'Bupivacaine (liposome)', 'Cardiovascular Diseases', 'Bupivacaine and other amide- containing products should be used with caution in patients with impaired cardiovascular function, as they may be less able to compensate for functional changes associated with the prolongation of AV conduction produced by these drugs.  Toxic blood concentrations can depress cardiac conductivity and excitability, which can lead to atrioventricular block, ventricular arrhythmias, and cardiac arrest.  In addition, myocardial contractility is depressed and peripheral vasodilation occurs, leading to decreased cardiac output and arterial blood pressure.', '2', '', 'DDInter', 0);
