INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34239, 1801, 'Buspirone', 'Liver Diseases', 'Buspirone is primarily metabolized by the liver and subsequently eliminated by the kidney.  In one study, steady-state area under the plasma concentration-time curve (AUC) of buspirone increased 13-fold in subjects with hepatic impairment and 4-fold in those with renal impairment compared to healthy subjects.  Therapy with buspirone is not recommended in the presence of significantly impaired hepatic or renal function.', '3', '', 'DDInter', 0);
