INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34292, 12548, 'Bupropion', 'Glaucoma, Angle-Closure', 'The pupillary dilation that occurs after use of many antidepressant drugs (including bupropion) can induce increased intraocular pressure and result in angle-closure (narrow-angle) glaucoma in a patient with anatomically narrow angles who does not have a patent iridectomy.  Patients may want to be examined to determine whether they are susceptible to angle closure, and have a prophylactic procedure (e.g., iridectomy), if they are susceptible.  The use of antidepressants (including bupropion-dextromethorphan) should be avoided in patients with untreated anatomically narrow angles.', '2', '', 'DDInter', 0);
