INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34297, 12548, 'Bupropion', 'Liver Diseases', 'Bupropion is primarily metabolized by the liver.  The systemic exposure and half-life of bupropion and its metabolites may be increased in patients with liver dysfunction.  A reduced dose and/or dosing frequency should be considered in patients with mild liver dysfunction (Child-Pugh score:  5 to 6).  A dosage reduction is required in patients with moderate to severe liver dysfunction (Child-Pugh score:  7 to 15) who are using a single-ingredient bupropion product; the dosage should not exceed 75 mg/day with immediate-release bupropion hydrochloride (HCl), 100 mg/day or 150 mg every other day with sustained-release bupropion HCl, 150 mg every other day with extended-release bupropion HCl, and 174 mg every other day with extended-release bupropion hydrobromide.  Bupropion-dextromethorphan is not recommended for patients with severe liver dysfunction (Child-Pugh C), and no dose adjustment of bupropion-dextromethorphan is recommended in patients with mild or moderate liver dysfunction (Child-Pugh A or B).', '2', '', 'DDInter', 0);
