INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34307, 12548, 'Bupropion', 'Psychotic Disorders', 'Depressed patients treated with bupropion have had various neuropsychiatric signs/symptoms, including delusions, hallucinations, psychosis, concentration disturbance, confusion, and paranoia; some of these patients had a diagnosis of bipolar disorder.  In some cases, these symptoms abated with dose reduction and/or discontinuation of therapy.  Bupropion should be discontinued if such reactions occur.', '2', '', 'DDInter', 0);
