INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34601, 14477, 'Caffeine', 'Cardiotoxicity', 'Like other methylxanthines, caffeine at high dosages may be associated with positive inotropic and chronotropic effects on the heart.  Caffeine may also produce an increase in systemic vascular resistance, resulting in elevation of blood pressure.  Therapy with products containing caffeine should be administered cautiously in patients with severe cardiac disease, hypertension, hyperthyroidism, or acute myocardial injury.  Some clinicians recommend avoiding caffeine in patients with symptomatic cardiac arrhythmias and/or palpitations and during the first several days to weeks after an acute myocardial infarction.', '2', '', 'DDInter', 0);
