INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34665, 1841, 'Caffeine', 'Water-Electrolyte Imbalance', 'Sodium chloride can induce sodium and water retention and result in hypernatremia, hypokalemia, edema and aggravation of congestive heart failure.  Therapy with sodium- containing agents should be administered with extreme caution, if at all, in patients with congestive heart failure, severe renal insufficiency, and in clinical states where there is edema with sodium retention.  Clinical monitoring of acid/base balance and electrolytes prior to, during, a following completion of therapy with sodium- containing agents is recommended.', '3', '', 'DDInter', 0);
