INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34729, 15638, 'Calcitriol', 'Kidney Diseases', 'Ergocalciferol, cholecalciferol, and calcifediol undergo renal biotransformation during metabolic activation.  Renal impairment can alter metabolic and therapeutic activity of certain vitamin D analogs.  Alternative vitamin D analogs such as dihydrotachysterol (hepatic activation) and calcitriol (active form) may be considered in patients with compromised renal function.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34730, 15638, 'Calcitriol', 'Diseases requiring dialysis', 'Calcitriol increases inorganic phosphate levels in serum and caution should be used in patients with renal impairment to maintain adequate phosphorus levels and avoid ectopic calcification.  A non-aluminum phosphate-binding compound and a low phosphate diet should be used to control serum phosphorus levels in patients undergoing dialysis.', '2', '', 'DDInter', 0);
