INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34797, 13907, 'Adalimumab', 'Neoplasms', 'In clinical trials malignancies have been observed in patients receiving TNF-blockers agents.  Patients with psoriasis should be monitored for non-melanoma skin cancers (NMSCs), particularly those patients who have had prior prolonged phototherapy treatment.  Melanoma and Merkel cell carcinoma have been reported in patients treated with TNF blocker therapy.  Periodic skin examination is recommended for all patients, particularly those with risk factors for skin cancer.  Postmarketing cases of fatal hepatosplenic T-cell lymphoma (HSTCL) have been reported in patients treated with TNF blockers.  Caution should be exercised when considering these agents in the treatment of patients with a history of malignancies or in continuing treatment in patients who develop malignancies while receiving these agents.', '3', '', 'DDInter', 0);
