INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34812, 13907, 'Adalimumab', 'Heart Failure', 'Cases of worsening congestive heart failure (CHF) and new onset CHF have been reported with TNF blockers.  Cases of worsening CHF have also been observed with adalimumab.  Adalimumab has not been formally studied in patients with CHF; however, in clinical trials of another TNF blocker, a higher rate of serious CHF-related adverse reactions was observed.  Exercise caution when using adalimumab in patients who have heart failure and monitor them carefully.', '2', '', 'DDInter', 0);
