INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34844, 3595, 'Calcium acetate', 'Sarcoidosis', 'Hypercalciuria, with or without hypercalcemia, may occasionally occur in patients with sarcoidosis.  Elevated calcium levels may result from increased intestinal absorption of calcium, which is related to the extrarenal production of vitamin D by mononuclear phagocytes present within the sarcoid granuloma.  Therapy with calcium salts should be administered cautiously and only if necessary in patients with sarcoidosis.', '3', '', 'DDInter', 0);
