INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34847, 1872, 'Calcium citrate', 'Hyperphosphatemia', 'Elevated serum concentrations of calcium and phosphate can exceed the solubility level and result in calcium- phosphate precipitates that deposit in vascular and renal systems as well as other soft tissues of the body.  Therapy with calcium should be administered with extreme caution in patients with hyperphosphatemia (hypoparathyroidism or severe renal impairment).  Administration of oral calcium acetate or calcium carbonate, in addition to providing calcium, complexes phosphates within the GI tract.  These complexes are eliminated in the feces.  Clinical monitoring of serum calcium and phosphate concentrations is necessary.', '3', '', 'DDInter', 0);
