INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34897, 1872, 'Calcium citrate', 'Malabsorption Syndromes', 'Calcium is absorbed from the intestinal tract by active transport and passive diffusion.  Malabsorption syndromes (celiac disease, GI resection), deficiency of vitamin D, parathyroid hormone, or calcitonin, or an alkaline gastric pH (achlorhydria, carbonate or phosphate salts) can decrease the absorption of oral formulations of calcium.  Calcium is available in oral and parenteral formulations.', '3', '', 'DDInter', 0);
