INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (35063, 15781, 'Capecitabine', 'Dehydration', 'Dehydration has been observed in patients receiving capecitabine and it may cause acute renal failure which could be fatal.  Patients with asthenia, anorexia, nausea, vomiting or diarrhea may rapidly become dehydrated.  Patients should be monitored to prevent and correct dehydration at the onset.  If dehydration grade 2 or higher occurs, treatment should be interrupted until dehydration is corrected.  Since capecitabine can also induce diarrhea, sometimes severe, patients should be carefully monitored and receive fluid and electrolyte replacement.  Standard antidiarrheal treatments are recommended.', '2', '', 'DDInter', 0);
