INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (35068, 15781, 'Capecitabine', 'Liver Diseases', 'Capecitabine is extensively metabolized by the liver.  In patients with mild to moderate liver dysfunction due to liver metastases the Cmax and AUC of capecitabine were increased 60%.  These patients should be carefully monitored when capecitabine is administered.  The effect of severe hepatic dysfunction on capecitabine has not been assessed.  Clinical monitoring of hepatic function is recommended for patients with compromised hepatic function.', '2', '', 'DDInter', 0);
