INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (35271, 2043, 'Carboplatin', 'Kidney Diseases', 'Carboplatin is primarily eliminated by the kidney.  Total body and renal clearance of carboplatin are reduced in patients with a creatinine clearance < 60 mL/min.  Patients with impaired renal function are at increased risk for toxicity, particularly myelosuppression.  Therapy with carboplatin should be administered cautiously in patients with compromised renal function.  Clinical monitoring of renal function is recommended.', '3', '', 'DDInter', 0);
