INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (35291, 2043, 'Carboplatin', 'Peripheral Nervous System Diseases', 'Mild peripheral neuropathy has been noted during carboplatin therapy and is characterized most frequently by paresthesias.  Patients older than 65 years and/or previously treated with cisplatin appear to be at increased risk for peripheral neuropathy.  There was no worsening of cisplatin symptoms in 70% of patients who received carboplatin as secondary treatment.  Therapy with carboplatin should be administered cautiously in patients with or predisposed to neuropathy.', '1', '', 'DDInter', 0);
