INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (35697, 1262, 'Cefaclor', 'Epilepsy', 'Cephalosporins have been implicated in triggering seizures.  Nonconvulsive status epilepticus (NCSE), encephalopathy, coma, asterixis, neuromuscular excitability, and myoclonia have been reported with cephalosporins particularly in patients with a history of epilepsy and/or when recommended dosages of cephalosporins were exceeded due to renal impairment.  Adjust dosing based on creatinine clearance.  Anticonvulsant therapy should be continued in patients with known seizure disorders.  If CNS adverse reactions including seizures occur, patients should undergo a neurological evaluation to determine whether treatment should be discontinued.', '2', '', 'DDInter', 0);
