INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (35725, 2058, 'Carvedilol', 'Atrioventricular Block', 'The use of beta-adrenergic receptor blocking agents (aka beta-blockers) is contraindicated in patients with sinus bradyarrhythmia or heart block greater than the first degree (unless a functioning pacemaker is present).  Due to their negative inotropic and chronotropic effects on the heart, the use of beta-blockers is likely to exacerbate these conditions.', '3', '', 'DDInter', 0);
