INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (35857, 2058, 'Carvedilol', 'Heart Failure', 'The use of carvedilol is contraindicated in patients with NYHA class IV decompensated heart failure.  Sympathetic stimulation may be important in maintaining the hemodynamic function in these patients, thus beta-blockade can worsen the heart failure.  Carvedilol may be used for the treatment of mild to moderate (NYHA class II or III) heart failure of ischemic or cardiomyopathic origin in patients receiving digitalis, diuretics, an ACE inhibitor, and/or nitrates.', '3', '', 'DDInter', 0);
