INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (36570, 914, 'Carbamazepine', 'Anticholinergic Syndrome', 'Carbamazepine is structurally related to the tricyclic antidepressants and has shown mild anticholinergic activity.  Therapy with carbamazepine should be administered cautiously in patients with conditions that may be exacerbated by anticholinergic activity, such as urinary retention or obstruction and elevated intraocular pressure, especially angle-closure glaucoma, untreated intraocular hypertension, or uncontrolled primary open-angle glaucoma.', '2', '', 'DDInter', 0);
