INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (37306, 51, 'Ceftriaxone', 'Liver Diseases', 'Ceftriaxone is eliminated by both renal and hepatobiliary excretion.  At usual dosages (i.e. 1 to 2 g/day), adjustments are generally not necessary in either renal or hepatobiliary impairment.  However, serum drug concentrations should be monitored periodically, and the dosage decreased accordingly if drug accumulation occurs.  In patients with both hepatic and severe renal impairment, ceftriaxone dosage should not exceed 2 grams per day without close monitoring of serum concentrations.', '2', '', 'DDInter', 0);
