INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38217, 2224, 'Cefoperazone', 'Hypoprothrombinemias', 'Hypoprothrombinemia, with or without bleeding, has been reported rarely with various cephalosporins, particularly those containing an N-methylthiotetrazole (NMTT) side chain (cefamandole, cefmetazole, cefoperazone, cefotetan).  The sulfhydryl group of this side chain is suspected of interfering with the hepatic synthesis of prothrombin.  Risk factors include advanced age, debility, vitamin K deficiency, malnutrition, malabsorption, and severe renal or hepatobiliary impairment.  Therapy with cephalosporins containing the NMTT side chain should be administered cautiously in patients with any of these risk factors and/or significant active bleeding or a hemorrhagic diathesis.  Prophylactic administration of vitamin K may be indicated in some patients, especially when intestinal sterilization and surgical procedures are performed.', '3', '', 'DDInter', 0);
