INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38237, 2224, 'Cefoperazone', 'Liver Diseases', 'Cefoperazone is primarily eliminated by hepatobiliary excretion.  In patients with impaired hepatic function and/or biliary obstruction, the half-life of cefoperazone is prolonged 2- to 4-fold but urinary excretion is increased.  At usual dosages, adjustments are generally not necessary in either renal or hepatobiliary impairment.  However, serum drug concentrations should be monitored if high dosages are used (i.e. > 4 g/day), and the dosage decreased accordingly if drug accumulation occurs.  In patients with both hepatic and severe renal impairment, cefoperazone dosage should not exceed 1 to 2 grams per day without close monitoring of serum concentrations.', '2', '', 'DDInter', 0);
