INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38297, 2224, 'Cefoperazone', 'Alcoholic Intoxication', 'Disulfiram-like reactions may occur in patients who consume alcohol within 72 hours after administration of a cephalosporin antibiotic that contains an N-methylthiotetrazole (NMTT) side chain (cefamandole, cefmetazole, cefoperazone, cefotetan).  The reaction appears to result from accumulation of acetaldehyde due to inhibition of acetaldehyde dehydrogenase.  Therapy with cephalosporins containing the NMTT side chain should be administered cautiously in patients with a history of alcoholism.  Patients should be instructed to avoid alcohol-containing products during therapy and up to 72 hours after the last dose.', '2', '', 'DDInter', 0);
