INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38323, 0, 'Cefotetan', 'Liver Diseases', 'Cases of hepatitis have been reported with the use of certain cephalosporins.  Transient rise in AST, ALT, and alkaline phosphatase levels have also been observed.  Caution and monitoring is recommended when these agents are prescribed to patients with hepatic disorders.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38324, 0, 'Cefotetan', 'Epilepsy', 'Cephalosporins have been implicated in triggering seizures.  Nonconvulsive status epilepticus (NCSE), encephalopathy, coma, asterixis, neuromuscular excitability, and myoclonia have been reported with cephalosporins particularly in patients with a history of epilepsy and/or when recommended dosages of cephalosporins were exceeded due to renal impairment.  Adjust dosing based on creatinine clearance.  Anticonvulsant therapy should be continued in patients with known seizure disorders.  If CNS adverse reactions including seizures occur, patients should undergo a neurological evaluation to determine whether treatment should be discontinued.', '2', '', 'DDInter', 0);
