INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38415, 2233, 'Ceftazidime', 'Heart Failure', 'Fortaz, Tazicef, and Tazidime (brands of parenteral ceftazidime pentahydrate) are formulated with sodium carbonate and contain approximately 53 mg (2.3 mEq) of sodium per each gram of ceftazidime activity.  The sodium content should be considered in patients with conditions that may require sodium restriction, such as congestive heart failure, hypertension, and fluid retention.  Ceptaz, another brand of ceftazidime, is formulated with l-arginine and contains no sodium.', '2', '', 'DDInter', 0);
