INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38603, 468, 'Albendazole', 'Neurologic Manifestations', 'Albendazole therapy of neurocysticercosis can induce an inflammatory response within the CNS that precipitates seizures and cerebral hypertension.  Therapy with albendazole for neurocysticercosis should be administered cautiously in patients with or predisposition to seizures.  The use of corticosteroids during the first week of therapy to prevent cerebral hypertension and anticonvulsant therapy as required is recommended.', '2', '', 'DDInter', 0);
