INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38663, 149, 'Chloramphenicol', 'Liver Diseases', 'Chloramphenicol is primarily inactivated by glucuronyl transferase in the liver and eliminated in the urine as both parent drug and metabolites.  In adults with normal renal and hepatic function, only 5% to 15% of a dose of chloramphenicol is excreted unchanged by the kidney, but approximately 30% is excreted when chloramphenicol is administered intravenously as the sodium succinate ester.  However, the fraction of drug excreted unchanged may be highly variable, especially in neonates and children.  Therapy with chloramphenicol should be administered cautiously in patients with significantly impaired renal and/or hepatic function, since drug accumulation may occur in such patients.  The dosage should be reduced based on the degree of impairment as well as plasma drug concentrations.', '3', '', 'DDInter', 0);
