INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38769, 306, 'Chloroquine', 'Arrhythmias, Cardiac', 'The use of 4-aminoquinolines such as hydroxychloroquine and chloroquine prolong the QT interval.  Ventricular arrhythmias and torsades de pointes have been reported in patients taking these agents.  Care should be exercised in patients with a prolonged QT interval at baseline or at increased risk for arrythmia.  A baseline electrocardiogram should be obtained to assess for QT interval prolongation and other abnormalities.  It is recommended to use appropriate diagnostic tools such as ECG to monitor patients during therapy.', '2', '', 'DDInter', 0);
