INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39423, 7108, 'Chlorpromazine', 'Alcoholic Intoxication', 'The use of phenothiazines is contraindicated in patients with acute alcohol intoxication exhibiting depressed vital signs.  The central nervous system depressant effects of phenothiazines may be additive with those of alcohol.  Severe respiratory depression and respiratory arrest may occur.  Therapy with phenothiazines should be administered cautiously in patients who might be prone to acute alcohol intake.', '3', '', 'DDInter', 0);
