INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39463, 7108, 'Chlorpromazine', 'Breast Neoplasms', 'The chronic use of phenothiazines is associated with persistent elevations in prolactin levels.  Based on in vitro data, approximately one-third of human breast cancers are thought to be prolactin-dependent.  The clinical significance of this observation is unknown.  Chronic administration of neuroleptic drugs has been associated with mammary tumorigenesis in rodent studies but not in human clinical or epidemiologic studies.  Therapy with phenothiazines should be administered cautiously in patients with existing or suspected malignancy of the breast.', '2', '', 'DDInter', 0);
