INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39568, 2520, 'Cholestyramine', 'Acidosis', 'Bile acid sequestrants are chloride forms of anion exchange resins and may produce hyperchloremic acidosis with chronic use.  Therapy with bile acid sequestrants should be administered cautiously in patients who may be particularly susceptible, including children or smaller patients and patients with renal impairment or volume depletion.', '2', '', 'DDInter', 0);
