INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39574, 2520, 'Cholestyramine', 'Nutrition disorder', 'Bile acid sequestrants may interfere with the absorption of folic acid and fat soluble vitamins such as A, D, and K.  Chronic use of bile acid sequestrants may cause increased bleeding tendency due to hypoprothrombinemia associated with vitamin K deficiency.  Anemia may also occur due to reduced serum or red blood cell folate.  Supplementation with oral vitamins and/or folate should be considered during prolonged therapy with bile acid sequestrants, particularly in patients with preexisting vitamin and/or folate deficiencies, anemia, or a bleeding diathesis.', '2', '', 'DDInter', 0);
