INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39576, 25538, 'Cholestyramine', 'Nutrition disorder', 'Bile acid sequestrants may interfere with the absorption of folic acid and fat soluble vitamins such as A, D, and K.  Chronic use of bile acid sequestrants may cause increased bleeding tendency due to hypoprothrombinemia associated with vitamin K deficiency.  Anemia may also occur due to reduced serum or red blood cell folate.  Supplementation with oral vitamins and/or folate should be considered during prolonged therapy with bile acid sequestrants, particularly in patients with preexisting vitamin and/or folate deficiencies, anemia, or a bleeding diathesis.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39577, 274, 'Cinacalcet', 'Hypocalcemia', 'Cinacalcet lowers serum calcium, and therefore patients should be carefully monitored for the occurrence of hypocalcemia.  Potential manifestations of hypocalcemia include paresthesias, myalgias, cramping, tetany, and convulsions.  Cinacalcet treatment should not be initiated if serum calcium is less than the lower limit of the normal range (8.4 mg/dL).  Monitor serum calcium 1 week after treatment initiation or dose adjustment, and once every month once the maintenance dose has been established.', '2', '', 'DDInter', 0);
