INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39598, 23011, 'Cisapride', 'Heart Diseases', 'The use of cisapride is contraindicated in patients with cardiac disease.  Serious cardiac arrhythmias, including ventricular arrhythmias and torsades de pointes associated with QT prolongation have been rarely associated with the use of cisapride, especially when taken in conjunction with other medications known to prolong the QT interval.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39599, 23011, 'Cisapride', 'Gastrointestinal Diseases', 'The use of cisapride is contraindicated in patients with gastrointestinal hemorrhage, mechanical obstruction, or perforation.  Enhanced gastrointestinal motility could be harmful in these patients.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39600, 24473, 'Chlorpropamide', 'Cardiovascular Diseases', 'The use of oral hypoglycemic agents may be associated with an increased risk of cardiovascular mortality compared to treatment with diet alone or diet with insulin.  This warning is based on the University Group Diabetes Program (UGDP) study, a long-term prospective clinical trial designed to evaluate the effectiveness of glucose-lowering drugs in preventing or delaying vascular complications in patients with non-insulin-dependent diabetes.  Patients treated with diet plus a fixed dosage of either tolbutamide (a sulfonylurea) or phenformin (a biguanide) for 5 to 8 years had a cardiovascular mortality rate approximately 2.5 times that of patients treated with diet alone, resulting in discontinuation of both these treatments in the study.  Despite controversy regarding interpretation of these results, clinicians and patients should be aware of the potential risk when making treatment decisions for diabetes, particularly in the presence of underlying cardiovascular disease.  Data are not available for other sulfonylureas or biguanides, nor for hypoglycemic agents belonging to other classes.  However, given the similarities in chemical structure and/or mode of action, the same caution should be applied.', '3', '', 'DDInter', 0);
